def get_login_url(self, scopes: list[str], state: str) -> str:

    params = {
        "client_id": self.client_id,
        "redirect_uri": self.redirect_uri,
        "response_type": "code",
        "owner": "user",
        "state": state,
    }

    return f"{self.auth_base_url}?{urlencode(params)}"

