class GetWikipediaSummaryBlock(Block, GetRequest):

    class Input(BlockSchema):
        topic: str = SchemaField(description='The topic to fetch the summary for')

    class Output(BlockSchema):
        summary: str = SchemaField(description='The summary of the given topic')
        error: str = SchemaField(description='Error message if the summary cannot be retrieved')

    def __init__(self):
        super().__init__(id='f5b0f5d0-1862-4d61-94be-3ad0fa772760', description='This block fetches the summary of a given topic from Wikipedia.', categories={BlockCategory.SEARCH}, input_schema=GetWikipediaSummaryBlock.Input, output_schema=GetWikipediaSummaryBlock.Output, test_input={'topic': 'Artificial Intelligence'}, test_output=('summary', 'summary content'), test_mock={'get_request': lambda url, json: {'extract': 'summary content'}})

    def run(self, input_data: Input, **kwargs) -> BlockOutput:
        topic = input_data.topic
        url = f'https://en.wikipedia.org/api/rest_v1/page/summary/{topic}'
        response = self.get_request(url, json=True)
        if 'extract' not in response:
            raise RuntimeError(f'Unable to parse Wikipedia response: {response}')
        yield ('summary', response['extract'])

class Output(BlockSchema):
    summary: str = SchemaField(description='The summary of the given topic')
    error: str = SchemaField(description='Error message if the summary cannot be retrieved')
