def util_color(graph: list[list[int]], max_colors: int, colored_vertices: list[int], index: int) -> bool:
if index == len(graph):
return True

for i in range(max_colors):
if valid_coloring(graph[index], colored_vertices, i):
colored_vertices[index] = i
if util_color(graph, max_colors, colored_vertices, index + 1):
return True

colored_vertices = [-1] * len(graph)
if util_color(graph, max_colors, colored_vertices, 0):
return colored_vertices

def graph_coloring(graph, m):
n = len(graph)
colors = [-1] * n
def is_safe(v, c):
for i in range(n):
if graph[v][i] == 1 and colors[i] == c:
return False

return True
def graph_coloring_util(v):
if v == n:
return True

for c in range(1, m+1):
if is_safe(v, c):
colors[v] = c
if graph_coloring_util(v + 1):
return True
